(() => {
  const rows = Table1.filteredTableData;

  // Normalisation (minuscules + suppression des accents + trim)
  const norm = (s) =>
  String(s ?? "")
  .toLowerCase()
  .normalize("NFD").replace(/[\u0300-\u036f]/g, "")
  .trim();

  const ofOuverts  = rows.filter(r => norm(r.statut_calcule) === "ouvert").length;
  const ofEnCours  = rows.filter(r => {
    const v = norm(r.statut_calcule);
    return v === "en cours" || v === "encours";
  }).length;
  const ofClotures = rows.filter(r => {
    const v = norm(r.statut_calcule);
    return v === "cloture" || v === "ferme force" || v.startsWith("clotur");
  }).length;

  return [
    { x: "OF Ouverts",   y: ofOuverts },
    { x: "OF En cours",  y: ofEnCours },
    // { x: "Clôturés",  y: ofClotures }
  ];
})()
}}

